<x-app-layout>
  @if($domains->count() > 0)
  <div class="flex flex-col w-full px-2 pt-3 h-max xl:overflow-hidden">
    <!---->
    <div class="flex flex-col gap-10">
      <div class="flex flex-col items-start justify-between gap-5 md:flex-row">
        <div class="flex flex-col items-start justify-start gap-5">
          <div class="flex flex-col gap-4">
            <!---->
            <div class="flex flex-row items-center gap-2 pl-1">
              <div class="flex items-center gap-5 text-center flex-wrap">
                <h1 class="text-title font-bold text-primary">DOMÍNIOS</h1>
                <!---->
                <p class="text-small text-secondary">({{ $domains->count() }} Domínio)</p>
              </div>
            </div>
            <p class="pl-1 text-regular text-primary">Vincule sua loja a um domínio</p>
          </div>
        </div>
        <div class="flex gap-3">
          <div class="flex select-none items-center justify-center gap-4 rounded bg-[#ff0071] text-white py-2.5 px-3 text-regular text-uncontrastColor disabled:pointer-events-none disabled:opacity-50">
            <p class="flex items-center gap-3">Domínios <span class="p-1 rounded-sm bg-white text-highlighted">{{ $domains->count() }}/1</span>
            </p>
          </div>
        </div>
      </div>
      <div class="flex flex-col items-start justify-start outline-none p-0 border-0 bg-transparent gap-2 w-fullrounded text-regular text-primary" type="text">
        <div class="flex w-full items-center justify-center gap-2 rounded border p-2 border-gray-300 bg-uncontrastColor">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true" data-slot="icon" class="h-6 w-6 text-primary">
            <path stroke-linecap="round" stroke-linejoin="round" d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z"></path>
          </svg>
          <input type="text" placeholder="Buscar por domínio..." class="w-full rounded-md border-0 bg-transparent text-regular text-primary outline-none disabled:cursor-not-allowed">
        </div>
        <!---->
      </div>
      <div class="flex flex-col gap-5">
        <div id="table-container" class="table-container w-full overflow-x-auto overscroll-x-none rounded">
          <!---->
          <table class="w-full table-auto max-lg:min-w-max">
            <thead>
              <tr>
                <th class="border-b border-tableBorder bg-tableHeader px-[6px] pb-[20px] pt-[20px] text-regular uppercase text-primary text-left">
                  <span class="min-w-max">DOMÍNIO</span>
                </th>
                <th class="border-b border-tableBorder bg-tableHeader px-[6px] pb-[20px] pt-[20px] text-regular uppercase text-primary text-left">
                  <span class="min-w-max">STATUS</span>
                </th>
                <th class="border-b border-tableBorder bg-tableHeader px-[6px] pb-[20px] pt-[20px] text-regular uppercase text-primary text-left">
                  <span class="min-w-max">SSL</span>
                </th>
                <th class="border-b border-tableBorder bg-tableHeader px-[6px] pb-[20px] pt-[20px] text-regular uppercase text-primary text-left">
                  <!---->
                </th>
              </tr>
            </thead>
            <tbody>
              @foreach ($domains as $domain)
                <tr class="border-b border-tableBorder bg-uncontrastColor clickable">
                  <td class="break-all px-[6px] pb-[30px] pt-[30px] text-regular text-primary text-left">{{ $domain->domain }}</td>
                  <td class="break-all px-[6px] pb-[30px] pt-[30px] text-regular text-primary text-left">
                    <div class="inline-flex items-center px-3 py-1 rounded text-regular text-statusDelivered bg-statusDelivered-100">Ativo</div>
                  </td>
                  <td class="break-all px-[6px] pb-[30px] pt-[30px] text-regular text-primary text-left">
                    <div class="inline-flex items-center px-3 py-1 rounded text-regular text-statusDelivered bg-statusDelivered-100">Ativo</div>
                  </td>
                  <td class="break-all px-[6px] pb-[30px] pt-[30px] text-regular text-primary text-left flex justify-end w-0">
                    <button class="text-regular select-none flex justify-center items-center gap-4 py-2.5 px-3 rounded disabled:opacity-50 disabled:pointer-events-none border-[#ff0071] text-[#ff0071] hover:bg-[#ff0071] hover:text-white outline button-action min-w-max max-md:w-full" type="submit" style="--color: var(--highlighted); --textColor: var(--highlighted); --hoverColor: var(--highlighted); --hoverTextColor: var(--background);"> Remover</button>
                  </td>
                </tr>
              @endforeach
            </tbody>
          </table>
        </div>
       
      </div>
    </div>
    <div class="pt-16"></div>
  </div>
  @else
  <div class="flex flex-col w-full px-2 pt-3 h-max xl:overflow-hidden" x-data="{ open: false }">
    <!-- Modal -->
    <div 
        x-show="open"
        x-transition:enter="transition ease-out duration-300 transform"
        x-transition:enter-start="opacity-0 scale-95"
        x-transition:enter-end="opacity-100 scale-100"
        x-transition:leave="transition ease-in duration-200 transform"
        x-transition:leave-start="opacity-100 scale-100"
        x-transition:leave-end="opacity-0 scale-95"
        class="fixed inset-0 bg-black bg-opacity-50 z-40 flex justify-center items-center"
    >
        <form method="POST" action="{{ route('store.config.domain') }}" class="bg-white w-full max-w-md mx-auto rounded-lg shadow-lg overflow-hidden">
            @csrf
            <div class="bg-primary text-white py-4 px-6 flex justify-between items-center">
                <span class="text-lg text-gray-700 font-semibold">ADICIONAR NOVO DOMÍNIO</span>
                <button @click="open = false" type="button" class="text-gray-600 hover:text-gray-400">
                    <svg xmlns="http://www.w3.org/2000/svg" class="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path stroke-linecap="round" stroke-linejoin="round" d="M6 18L18 6M6 6l12 12"/>
                    </svg>
                </button>
            </div>
            <div class="p-6 space-y-4">
                <div class="text-sm text-gray-600">Domínio</div>
                <div class="flex flex gap-3">
                  <select class="w-2/4 p-3 border text-gray-500 border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary transition-all" name="sub_domain" id="">
                    <option value="checkout">checkout.</option>  
                  </select>  
                  <input 
                        type="text" 
                        placeholder="ex.: sualoja.com.br"
                        class="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary transition-all"
                        name="domain"
                    >
                </div>
                <small class="text-xs text-gray-500">Esse será o nome enviado nos e-mails da sua loja</small>
                <hr class="my-4 border-gray-200 opacity-40">
            </div>
            <div class="flex justify-end space-x-4 p-4">
                <button @click="open = false" type="button" class="text-sm text-gray-700 py-2 px-4 rounded-md border border-gray-300 hover:bg-gray-100 focus:outline-none transition">Cancelar</button>
                <button type="submit" class="text-sm text-white bg-[#ff0071] py-2 px-4 rounded-md focus:outline-none transition">Salvar</button>
            </div>
        </form>
    </div>

    <!-- Seção sem domínio -->
    <div>
        <div class="mt-6 flex w-full flex-row flex-wrap items-center justify-around gap-10 p-4 md:mt-16 md:mb-16 md:flex-nowrap md:p-12">
            <div class="basis-2/2 order-2 flex flex-row justify-center md:order-1 md:basis-1/2">
                <div class="flex w-full flex-col justify-center gap-8">
                    <h2 class="text-2xl font-bold leading-tight md:text-[28px]">Você ainda não tem nenhum <br> domínio cadastrado.</h2>
                    <div class="text-base">Cadastrando seu domínio você aumenta a <br> credibilidade e conversão da sua loja.</div>
                    <div class="flex flex-row items-center justify-center">
                        <button @click="open = true" class="h-14 rounded-md bg-[#F30168] px-6 font-medium uppercase text-white hover:bg-highlighted disabled:cursor-not-allowed disabled:bg-gray-400 disabled:opacity-30">CADASTRAR DOMÍNIO</button>
                    </div>
                </div>
            </div>
